import React, { useState,useEffect } from "react"
import { Button } from "react-bootstrap"
import './User.css'
import axios from "axios"

// memberGender: "0"
// memberId: 12
// memberImgPath: null
// memberMail: "gina@gmail.com"
// memberName: "沈吉娜"
// memberNickName: "GINA"
// memberPasswd: "gina"
// memberPhone: "0977444555"
// registerType: "0"
// updateDatetime: "2022-06-21 15:59:19"

export default function User(props) {

  const [userData, setUserData] = useState({})

  // 抓取表單資料使用 & 設立初始值
  const [formData, setFormData] = useState(
    {
      mail: "",
      name: userData.memberName,
      nickName: userData.memberNickNam,
      sex: userData.memberGender,
      phone:userData.memberPhone
    }
  )

  useEffect(()=>{
    axios({
      method: "post",
      url: "http://localhost:5000/member/isLogin",
      data:{
        token:localStorage.token
      }
    }).then((res) => {
      // console.log(res) //有登入的話，回傳「會員資訊」在res.data[0] ｜ 沒登入則回傳message
      let data = res.data[0];
      setUserData( prevUserData => data)//想辦法把值取出來
      console.log(data) //OK

    }).catch((err) => {
      console.log(err)
    })
},[])


console.log(userData.memberMail)//OK


  // 追蹤表單輸入值使用
  function inputHandler(e) {
    const { name, value } = e.target
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value
    }))
  }
  console.log(formData)


  return (
    <div className="User">
      <ul className="user--menu">
        <li><a href="/" className="menu--item--on">基本資料</a></li>
        <li><a href="/" className="menu--item">訂單記錄</a></li>
        <li><a href="/" className="menu--item">活動回饋</a></li>
        <li><a href="/" className="menu--item">優惠表單</a></li>
      </ul>

      <div className="user--card">
        <div className="user--icon">
          <div className="icon--pic">
            <img src="avatar-pl.png" alt="ICON" />
          </div>
          <Button className="user--btn--L" size="sm" variant="secondary">上傳照片</Button>
          <Button className="user--btn--L" size="sm" variant="secondary">更換封面</Button>
        </div>
        <ul className="user--form">
          <li className="user--item">
            <label htmlFor="mail">帳號</label>
            <input
              type="text"
              name="mail"
              id="mail"
              value={userData.memberMail}
              onChange={inputHandler}
            />
          </li>
          <li className="user--item">
            <label htmlFor="name">姓名</label>
            <input
              type="text"
              name="mail"
              id="mail"
              value={userData.memberName}
              onChange={inputHandler} />
          </li>
          <li className="user--item">
            <label htmlFor="nickName">暱稱</label>
            <input
              type="text"
              name="nickName"
              id="nickName"
              value={formData.nickName}
              onChange={inputHandler} />
          </li>
          <li className="user--item">
            <label htmlFor="sex">性別</label>
            <select
              name="sex"
              id="sex"
              value={formData.sex}
              onChange={inputHandler} >
              <option value="1">男性</option>
              <option value="0">女性</option>
            </select>
          </li>
          <li className="user--item">
            <label htmlFor="phone">手機</label>
            <input
              type="text"
              name="phone"
              id="phone"
              value={formData.phone}
              onChange={inputHandler} />
          </li>
        </ul>
        <div className="user--btnBar">
          <Button className="user--btn--M" size="s" variant="secondary">修改</Button>
          <Button className="user--btn--M" size="s" variant="secondary">儲存</Button>
        </div>
      </div>

    </div>
  )

}
